insert.sql:
insert into ut_schedule (Date,TournamentID, Tour, Comments, Event, Finished) values(
   @Date;,
   substring_index(@Tournament;,'|',1),
   substring_index(@Tournament;,'|',-1),
   @Comments;,
   @Event;,
   @Finished;
)

update.sql:
update ut_schedule set 
   TournamentID=substring_index(@Tournament;,'|',1),
   Tour=substring_index(@Tournament;,'|',-1),
   Date=@Date;,
   Comments=@Comments;
where TurID=@id;
