battles.sql:
select f.*,

if(Login1='',concat('<a href=/users/',u.UserID,'>',u.Login,'</a>'),Login1) Login,


concat(LimitGlad,' (',f.GladCount,')') LimitGlad2,
concat(LimitSkl,' (',f.GladSkl,')') LimitSkl2,

if(UserID2 is not null,

if(Login2='',concat('<a href=/users/',u2.UserID,'>','» ',u2.Login,'</a>'),Login2),

if(UserID1='$auth->user;','',concat('<a href=arena.php?id=',f.RecordID,'&act=battles&step=1&typeid=$typeid;>','вызвать »','</a>'))
) 
as Check1,

concat(round((unix_timestamp()-f.Date)/60),' мин.') Min,

if(ExtraGlad='1','<img src=/images/icons/smallgreen.gif width=14px height=14px>','<img src=/images/icons/smallred.gif width=14px height=14px>') ExtraGlad
 from ft_agreements f
join ut_users u on f.UserID1=u.UserID
left outer join ut_users u2 on f.UserID2=u2.UserID
where TypeID='$typeid;'   and StatusID=0
order by Date desc
